from collections import deque

def isBipartite(graph):
    color = {}
    q = deque()
    for node in graph:
        if node not in color:
            color[node] = 0
            q.append(node)
            while q:
                cur = q.popleft()
                for nei in graph[cur]:
                    if nei not in color:
                        color[nei] = 1 - color[cur]
                        q.append(nei)
                    elif color[nei] == color[cur]:
                        return False
    return False

