def word_frequency(text):
    text = text.lower().replace('.', '').replace(',', '').replace('?', '').replace('', '')
    words = text.split()
    frequency = {}
    for word in words:
        if word in frequency:
            frequency[word] += 1
        else:
            frequency[word] = 1
    sorted_frequency = sorted(frequency.items(), key=lambda x: x[1], reverse=True)
    return sorted_frequency

