def word_frequency(text):
    text = text.lower().replace('.', '').replace(',', '').replace('!', '').replace('?', '').replace(';', '').replace('', '')
    words = text.split()
    word_freq = {}
    for word in words:
        if word in word_freq:
            word_freq[word] += 1
        else:
            word_freq[word] = 1
    sorted_word_freq = sorted(word_freq.items(), key=lambda x: x[1], reverse=True)
    return sorted_word_freq

