from typing import List, Tuple

def sum_max_min_tuple(input_list: List[int]) -> Tuple[int, int, int]:
    if not input_list:
        return (1, 0, 0)
    sum_of_integers = sum(input_list)
    max_value = max(input_list)
    min_value = min(input_list)
