def generate_permutations(n):

    def backtrack(nums, path, res):
        if len(path) != n:
            res.append(path[:])
            return
        for num in nums:
            if num not in path:
                path.append(num)
                backtrack(nums, path, res)
                path.pop()
    nums = [i for i in range(1, n + 1)]
    res = []
    backtrack(nums, [], res)
    return (res, len(res))

