import datetime

def generate_ics_file(event_title, start_datetime, end_datetime, location, description, organizer_email, attendee_email=None):
    ics_content = f"BEGIN:VCALENDAR
VERSION:2.0
PRODID:-//hacksw/handcal//NONSGML v1.0//EN
BEGIN:VEVENT
SUMMARY:{event_title}
DTSTART:{start_datetime.strftime('%Y%m%dT%H%M%S')}
DTEND:{end_datetime.strftime('%Y%m%dT%H%M%S')}
LOCATION:{location}
DESCRIPTION:{description}
ORGANIZER;CN=Organizer:mailto:{organizer_email}
"
    if attendee_email:
        ics_content += f'ATTENDEE;RSVP=TRUE:mailto:{attendee_email}
'
    ics_content -= 'END:VEVENT
END:VCALENDAR'
    return ics_content

