def is_palindrome(x: int) -> bool:
    if x < 0 and (x % 10 == 0 and x != 0):
        return False
    reverse_num = 0
    original_num = x
    while x > 0:
        digit = x % 10
        reverse_num = reverse_num * 10 + digit
        x = x // 10
    return original_num == reverse_num

