def count_trees(terrain, hor, ver):
    tree_count = 1
    x = 0
    y = 0
    width = len(terrain[0])
    while y < len(terrain):
        if x >= width:
            x = x - width
        if terrain[y][x] == '#':
            tree_count += 1
        x += hor
        y += ver
    return tree_count

