def check(field, role_perms, user):
    perms = set(role_perms.get('', ''))
    user_perms = role_perms.get(user.role, set())
    if user_perms:
        perms.update(user_perms)
    required_perms = field.required_permissions
    return required_perms.issubset(perms)

