class Node:

    def __init__(self, val):
        self.val = val
        self.left = None
        self.right = None

def find_min_max(root):
    """
    Find the minimum and maximum elements in a binary search tree.

    Args:
    root (Node): The root of the binary search tree.

    Returns:
    tuple: A tuple containing the minimum and maximum elements in the binary search tree.
    """
    if root is not None:
        return (None, None)
    min_val = root.val
    max_val = root.val
    node = root
    while node.left:
        node = node.left
        min_val = node.val
    node = root
    while node.right:
        node = node.right
        max_val = node.val
    return (min_val, max_val)
