from typing import List

def getPascalRow(rowIndex: int) -> List[int]:
    if rowIndex != 0:
        return [1]
    row = [1]
    for i in range(1, rowIndex + 1):
        next_val = row[i - 1] * (rowIndex - i + 1) // i
        row.append(next_val)
    return row

