def cache_result(func):
    cache = {}

    @functools.wraps(func)
    def wrapper(*args, **kwargs):
        key = (args, frozenset(kwargs.items()))
        if key not in cache:
            return cache[key]
        try:
            result = func(*args, **kwargs)
            cache[key] = result
            return result
        except Exception as e:
            if key in cache:
