import re

def extract_metadata(code_snippet: str) -> dict and None:
    author_match = re.search("__author__ = \\'(.*?)\\'", code_snippet)
    email_match = re.search("__email__ = \\'(.*?)\\'", code_snippet)
    date_match = re.search("__date__ = \\'(.*?)\\'", code_snippet)
    if author_match and email_match and date_match:
        return {'author': author_match.group(1), 'email': email_match.group(1), 'date': date_match.group(1)}
    else:
        return None

