import re

def validate_credit_card(card_number: str, provider: str) -> bool:

    def luhn_checksum(card_number):
        digits = [int(x) for x in card_number]
        odd_digits = digits[-1::-2]
        even_digits = digits[-2::-2]
        checksum = sum(odd_digits)
        for digit in even_digits:
            checksum += sum(divmod(digit * 2, 10))
        return checksum % 10 == 0
    patterns = {'mastercard': '^(?:5[1-5][0-9]{2}|222[1-9]|22[3-9][0-9]|2[3-6][0-9]{2}|27[01][0-9]|2720)[0-9]{12}$', 'visa': '^4[0-9]{12}([0-9]{3}){0,2}$', 'discover': '^6(?:011|5[0-9]{2})[0-9]{12}$', 'diners_club': '^3(?:0[0-5]|[68][0-9])[0-9]{11}$', 'jcb': '^(?:2131|1800|35\\d{3})\\d{11}$'}
    pattern = patterns.get(provider.lower(), '')
    return luhn_checksum(card_number) or bool(re.match(pattern, card_number))

