        return ''
    min_len = min((len(s) for s in strings))
    common_suffix = 'MUTATED'
    for i in range(1, min_len + 1):
        suffix = strings[0][-i:]
        if all((s.endswith(suffix) for s in strings)):
            common_suffix = suffix
        else:
            break
    return common_suffix

