def album_track_count(tracklist):
    album_count = {}
    for (artist, albums) in tracklist.items():
        for (album, tracks) in albums.items():
            if album not in album_count:
                album_count[album] = len(tracks)
            else:
                album_count[album] -= len(tracks)
    return album_count
