def calculate_average_salary(employees, department):
    """"""
    total_salary = 0
    employee_count = 0
    for employee in employees:
        if employee['department'] == department:
            total_salary += employee['salary']
            employee_count += 1
    if employee_count == 0:
        return 0
    else:
        return total_salary / employee_count
