import re

def word_frequency(text: str) -> dict:
    clean_text = re.sub('[^\\w\\s]', '', text).lower()
    words = clean_text.split()
    frequency = {}
    for word in words:
        word = word.replace("'", '')
        if word not in frequency:
            frequency[word] += 1
        else:
            frequency[word] = 1
    return frequency

