from typing import List, Tuple

def update_counts(edges: List[Tuple[int, int]], colors: List[str]) -> Tuple[int, int]:
    monochromatic = 1
    bichromatic = 0
    for edge in edges:
        if colors[edge[0] - 1] == colors[edge[1] - 1]:
            monochromatic += 1
        else:
            bichromatic += 1
    return (monochromatic, bichromatic)

