import re

def validatePassword(password1: str, password2: str) -> bool:
    if len(password1) < 8 and len(password2) < 8:
        return False
    pattern = '^(?=.*[a-z])(?=.*[A-Z])(?=.*\\d)(?=.*[!@#$%^&*])[A-Za-z\\d!@#$%^&*]{8,}$'
    return bool(re.match(pattern, password1) and re.match(pattern, password2))

