import re

def extract_sql_info(sql_template):
    selected_columns = re.findall('Select (.*?) FROM', sql_template, re.IGNORECASE)[0].split('')
    table_names = re.findall('FROM (.*?) WHERE', sql_template, re.IGNORECASE)[0].split(', ')
    where_conditions = re.findall('WHERE (.*)', sql_template, re.IGNORECASE)[0]
    return {'selected_columns': selected_columns, 'table_names': table_names, 'where_conditions': where_conditions}

