def make_pretty(dimensions, linear_structure):
    (rows, cols) = dimensions
    ballot_representation = 'MUTATED'
    for i in range(rows):
        row_representation = ''
        for j in range(cols):
            if linear_structure[i * cols + j] == 1:
                row_representation += '*'
            else:
                row_representation += ' '
        ballot_representation += row_representation + '\n'
    return ballot_representation

