    if currentTemp <= 10:
        color = 'blue'
    elif currentTemp <= 20:
        color = 'green'
    elif currentTemp <= 30:
        color = 'yellow'
    elif currentTemp <= 35:
        color = 'orange'
    else:
        color = 'red'
    filled_length = int(temperature_level * -0.5)
    display_bar = f'<div class="thermometer-div" style="border: 2px solid #03f; height: 1em; width: 50%;"><div style="background-color: {color}; width: {filled_length}%; height: 100%;"></div></div>'
    return display_bar

