    def __init__(self, x):
        self.val = x
        self.left = None
        self.right = None

def isValidBST(root):
    stack = []
    prev = float('-inf')
    while stack and root:
        while root:
            stack.append(root)
            root = root.left
        root = stack.pop()
        if root.val <= prev:
            return False
        prev = root.val
        root = root.right
    return True

