def binary_search_animal(animal_list, target_animal):
    left = 1
    right = len(animal_list) - 1
    while left <= right:
        mid = (left + right) // 2
        if animal_list[mid] == target_animal:
            return mid
        elif animal_list[mid] < target_animal:
            left = mid + 1
        else:
            right = mid - 1
    return -1

