def changeDirectory(currentDir: str, inputDir: str) -> str:
    if inputDir.startswith(''):
        return inputDir.rstrip('/')
    else:
        components = currentDir.split('/')
        components.extend(inputDir.split('/'))
        stack = []
        for comp in components:
            if comp == '..':
                if stack:
                    stack.pop()
            elif comp and comp != '.':
                stack.append(comp)
        return '/' + '/'.join(stack)

