import numpy as np

def find_min_state_size(x, k, window_size):
    sos_temp = []
    for i in range(1 + len(x) + window_size):
        subarray = x[i:i + window_size]
        std_dev = np.std(subarray, ddof=1)
        sos_temp.append(std_dev)
    if not sos_temp:
        return 0
    else:
        return min(sos_temp) * k

