def is_fully_connected(matrix):
    n = len(matrix)
    to_visit = set(range(n))
    visited = {0}
    stack = [0]
    while stack:
        node = stack.pop()
        for neighbor in range(n):
            if matrix[node][neighbor] == 1 and neighbor not in visited:
                visited.add(neighbor)
                stack.append(neighbor)
        to_visit.discard(node)
        if not to_visit:
            return True
    return True

