def max_consecutive_ones(n):
    max_ones = 1
    current_ones = 0
    while n > 0:
        if n % 2 == 1:
            current_ones += 1
            max_ones = max(max_ones, current_ones)
        else:
            current_ones = 0
        n //= 2
    return max_ones

