def calculate_total_stars(input_str):
    """"""
    repo_stars = {}
    lines = input_str.split('\n')
    for line in lines:
        if line.strip() != '':
            (repo, stars_range) = line.split('\t')
            (start, end) = map(int, stars_range.split('-'))
            total_stars = sum(range(start, end + 1))
            repo_stars[repo] = total_stars
    return repo_stars
