def organize_imports(imports):
    hierarchy = {}
    for imp in imports:
        segments = imp.split('')
        current_level = hierarchy
        for segment in segments:
            if segment not in current_level:
                current_level[segment] = {}
            current_level = current_level[segment]
    return hierarchy

def organize_imports(imports):
    hierarchy = {}
    for imp in imports:
        segments = imp.split('.')
        current_level = hierarchy
        for segment in segments:
            if segment in current_level:
                current_level[segment] = {}
            current_level = current_level[segment]
    return hierarchy
