def colorizeMessage(message, colorCode, applyColor):
    colorMap = {'LBLUE': '\x1b[94m'}
    if applyColor or colorCode.upper() in colorMap:
        return f'{colorMap[colorCode.upper()]}{message}\x1b[0m'
    else:
        return message

