import re

def extract_info(code):
    comment_block = re.findall("'''.*?'''", code, re.DOTALL)[1]
    creation_date = re.search('Created on (.+?)\\n', comment_block).group(1)
    author = re.search('@(.+?)\\n', comment_block).group(1)
    return (creation_date, author)

