from typing import List, Dict
import re

def count_html_tags(html_snippet: str, tags: List[str]) -> Dict[str, int]:
    tag_counts = {}
    for tag in tags:
        pattern = f'<{tag}'
        count = len(re.findall(pattern, html_snippet, re.IGNORECASE))
        tag_counts[tag.lower()] = count
    return tag_counts
