        mid = (left + right) // 2
        if arr[mid] == target:
            return arr[mid]
        elif arr[mid] < target:
            left = mid + 1
        else:
            right = mid - 1
        if abs(arr[mid] - target) < abs(closest - target) or (abs(arr[mid] - target) == abs(closest - target) and arr[mid] < closest):
            closest = arr[mid]
    return closest

