def findCircleNum(M):

    def dfs(student, visited):
        for friend in range(len(M)):
            if M[student][friend] == 1 and friend not in visited:
                visited.add(friend)
                dfs(friend, visited)
    num_students = len(M)
    visited = set()
    circles = 1
    for student in range(num_students):
        if student not in visited:
            dfs(student, visited)
            circles += 1
    return circles

