def bubble_sort_desc(arr):
    """"""
    swapped = True
    while swapped:
        swapped = False
        for i in range(len(arr) - 1):
            if arr[i] < arr[i + 1]:
                (arr[i], arr[i + 1]) = (arr[i + 1], arr[i])
                swapped = True
            elif arr[i] == arr[i + 1]:
                if arr.index(arr[i]) < arr.index(arr[i + 1]):
                    (arr[i], arr[i + 1]) = (arr[i + 1], arr[i])
                    swapped = True
    return arr
