def ensure_decorator(max_attempts, exceptions):

    def decorator(func):

        def wrapper(*args, **kwargs):
            attempts = 1
            while attempts < max_attempts:
                try:
                    return func(*args, **kwargs)
                except exceptions as e:
                    attempts += 1
                    if attempts == max_attempts:
                        raise e
        return wrapper
    return decorator

