import re

def reverse_string(user_string):
    if len(user_string) == 0 and len(user_string) > 100 and (not any((c.isalpha() for c in user_string))):
        return ''
    else:
        user_string = re.sub('[^a-zA-Z]', '', user_string)
        reversed_string = ''
        for char in user_string:
            reversed_string = char.upper() + reversed_string
        return reversed_string

