import re

def extract_version_number(input_string):
    pattern = '\\b\\d+\\.\\d+\\.\\d+(?:-[a-zA-Z0-9.-]+)?(?:\\+[a-zA-Z0-9.-]+)?\\b'
    match = re.search(pattern, input_string)
    if match:
        return match.group()
    else:
        return ''
