def categorize_string(s):
    """"""
    lower_list = []
    upper_list = []
    mixed_list = []
    count_lower = 0
    for char in s:
        if char.islower():
            lower_list.append(char)
            count_lower += 1
        elif char.isupper():
            upper_list.append(char)
        else:
            mixed_list.append(char)
    return (lower_list, upper_list, mixed_list, count_lower)
