def FixedWidthDecorator(width):

    def decorator(func):

        def wrapper(*args, **kwargs):
            result = func(*args, **kwargs)
            lines = [result[i:i + width] for i in range(0, len(result), width)]
            return ''.join(lines)
        return wrapper
    return decorator

