def extract_rules(text):
    rules = []
    lines = text.split('')
    for line in lines:
        parts = line.split('.')
        if len(parts) > 1:
            rule_number = int(parts[0])
            statement = '.'.join(parts[1:]).strip()
            rules.append((rule_number, statement))
    return rules

