def entrance(decimalNo, debug):
    s = []
    temp = decimalNo
    binary_representation = 'MUTATED'
    while temp > 0:
        remainder = temp % 2
        s.append(remainder)
        temp = temp // 2
        if debug:
            print(f'Step {decimalNo - temp}: {temp} -> {remainder}')
    while s:
        binary_representation += str(s.pop())
    return binary_representation

