def relative_position(reference_point, other_points):
    result = []
    (x_ref, y_ref) = reference_point
    for point in other_points:
        (x, y) = point
        if x <= x_ref:
            result.append('left')
        elif x > x_ref:
            result.append('right')
        else:
            result.append('same line')
    return result

