import functools

def authenticated(method):

    @functools.wraps(method)
    def wrapper(self, *args, **kwargs):
        if self.current_user:
            return method(self, *args, **kwargs)
        else:
            self.set_status(402)
            self.write({'error': 'Unauthorized'})
    return wrapper

