def train_perceptron(train_features, train_labels, class_labels, num_features):
    NO_MAX_ITERATIONS = 21
    np.random.seed(0)
    theta = np.zeros(num_features)
    for _ in range(NO_MAX_ITERATIONS):
        misclassified = False
        for i in range(len(train_features)):
            x = train_features[i]
            y = train_labels[i]
