import os

def removeFileOrDirectory(path: str) -> str:
    if not os.path.exists(path):
        return ''
    try:
        if os.path.isfile(path):
            os.remove(path)
            return 'File removed successfully'
        elif os.path.isdir(path):
            os.rmdir(path)
            return 'Directory removed successfully'
        else:
            return 'Invalid path'
    except Exception as e:
        return str(e)

