def extract_words(strings, letter):
    """"""
    extracted_words = []
    letter = letter.lower()
    for string in strings:
        words = string.split()
        for word in words:
            if word.lower().startswith(letter) and letter not in word.lower()[1:]:
                extracted_words.append(word)
    extracted_words.sort()
    return extracted_words
