def quicksort(xs):
    if len(xs) < 1:
        return xs
    else:
        pivot = xs[0]
        xs = xs[1:]
        left = [x for x in xs if x <= pivot]
        right = [x for x in xs if x > pivot]
        return quicksort(left) + [pivot] + quicksort(right)

